<script setup>
const { $gsap } = useNuxtApp();
let ctx;
onMounted(() => {
  //   $gsap.to("#title", { rotation: 3, x: 100, duration: 1, ease: "expo.in" });
  //   $ScrollTrigger.create({
  //     trigger: "#title",
  //     x: 500,
  //     start: "top center",
  //     end: "bottom top",
  //     scrub: 2,
  //   });
  //   $gsap.to("#title", {
  //     scrollTrigger: "#title", // start animation when ".box" enters the viewport
  //     x: 200,
  //   });

  //
  ctx = $gsap.context((self) => {
    let tl = $gsap.timeline({
      // yes, we can add it to an entire timeline!
      scrollTrigger: {
        trigger: ".container",
        pin: true, // pin the trigger element while active
        start: "top top", // when the top of the trigger hits the top of the viewport
        end: "+=800", // end after scrolling 500px beyond the start
        scrub: 1, // smooth scrubbing, takes 1 second to "catch up" to the scrollbar
        markers: true,
      },
    });

    // add animations and labels to the timeline
    tl.addLabel("start").from("#title", { scale: 2, rotation: 45, autoAlpha: 0 }).addLabel("color").from("#title", { backgroundColor: "#28a92b" }).addLabel("spin").to("#title", { x: 500, duration: 2 }).addLabel("end");
    //
  });
});
onUnmounted(() => {
  ctx.revert();
});
</script>
